package cn.hm1006.boot.controller;import cn.hm1006.boot.entity.Air;import cn.hm1006.boot.mapper.AirMapper;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;import java.util.List;/** * @author : Two * @version : v1.0 * @description TODO * @date : 2021/3/1 17:51 */@RestController@RequestMapping("/air")public class AirController {    @Resource    private AirMapper airMapper;    @GetMapping("/getAll")    public List<Air> getAll(){        return airMapper.findAll();    }    @GetMapping("/{id}")    public Air getAir(@PathVariable("id") Integer id){        return airMapper.findbyId(id);    }}