package cn.hm1006.boot.controller;import cn.hm1006.boot.entity.User;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;/** * @author : Two * @version : v1.0 * @description TODO * @date : 2021/3/1 11:45 */@RestController@RequestMapping("/")public class HelloController {    @Resource    private User user;    @GetMapping("/hello")    public User hello(){        return user;    }    @GetMapping("/hello/{name}")    public User hello(@PathVariable("name") String name){        user.setName(name);        return user;    }}