package cn.hm1006.boot.controller;import cn.hm1006.boot.entity.User;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import javax.xml.crypto.Data;import java.util.ArrayList;import java.util.Date;/** * @author : Two * @version : v1.0 * @description TODO * @date : 2021/3/1 22:41 */@Controller@RequestMapping("/thymeleaf")public class ThymeleafController {    @RequestMapping("/hello")    public String toThymeleaf(Model model){        model.addAttribute("name", "张三");        model.addAttribute("age", 20);        User user = new User("张三",20);        User user2 = new User("李四",20);        User user3 = new User("王五",20);        ArrayList<User> list = new ArrayList<>();        list.add(user);        list.add(user2);        list.add(user3);        model.addAttribute("stus",list);        Date now = new Date();        model.addAttribute("now",now);        return "index";    }}